	public download(url: string, name: string) : Promise<any> {
	    return new Promise((resolve,reject)=>{
	        this.platform.ready().then(() => {
	            const fileTransfer: TransferObject = this.transfer.create();
	        this.c8o.log.error("preparing to download");
	            fileTransfer.download(url, this.storageDirectory + name)
	            .then((entry) => {
	                this.c8o.log.error("download ok");
	                resolve(true);
	            }, (error) => {
	                this.c8o.log.error("download ko1");
	                resolve(false);
	            }).catch((e)=>{
	                this.c8o.log.error("download ko2");
                    resolve(false);
                })
	          }); 
	    });

	  }

	  public checkforfiles(name: string) : Promise<boolean>{
	    return new Promise((resolve)=>{
	        let present: boolean = false
	        this.file.checkFile(this.storageDirectory, name)
	          .then(() => {
	              present = true;
	              resolve(present);
	          })
	          .catch((err) => {
	              present = false;
	              resolve(present);
	          });
	        //resolve(present);
	    }); 
	  }

	  public openPDF(name:string){
	      this.fileOpener.open(this.storageDirectory + name, 'application/pdf')
          .then(() => this.loading.dismiss())
          .catch((e)=> {
              this.loading.dismiss();
              let toast = this.toastCtrl.create({
                  message: "Erreur pendant l'ouverture du fichier",
                  duration: 3000,
                  position: 'bottom'
                });
              toast.present();
              console.log('Error openening file', e)
          } );
	  }

	  public search(value:string){
	      let array =[];
	      for(let element of this.listen(['fs://fs_monmobile.get#doc']).array){
	          if((element.name).indexOf(value)!= -1){
	              array.push(element);
	          }
	      }
	  }

